import base64
import os

def trans_yzm(base):
    '''base64转图片格式'''

    path = os.path.realpath(__file__)[:-25]
    # print(path)
    image = base
    fh = open(path + '/img/verificationCode.png',"wb")
    # fh = open('D:/verificationCode.png',"wb")
    fh.write(base64.b64decode(image))
    fh.close()

